import React from 'react';
import './../css/About.css'; // Import the CSS for styling

const AboutUs = () => {
  return (
    <section className="about-us">
      <div className="about-us-content">
        <h1>PLAN BEE ESTATE</h1>
        <p>
          Welcome to <strong>Plan Bee Estate Pvt Ltd</strong>, where we specialize in offering an exquisite range of 2BHK, 3BHK flats, luxury apartments, and penthouses in the heart of Gurgaon.
          We bring you properties from the most prestigious developments, including Smartworld 69, Signature, Trehan, and M3M Golf Hills. Whether you’re looking for a cozy home for your family or a luxurious space to unwind, our curated selections cater to every lifestyle need. Discover unparalleled living experiences with us—your gateway to upscale living in Gurgaon.
        </p>
        <p>
          At Plan Bee Estate, we prioritize customer satisfaction and transparency, offering end-to-end solutions from property search to seamless transactions. Explore the best real estate opportunities in one of the most vibrant cities in India.
        </p>
      </div>
      <div className="about-us-image">
        <img src="houses/house1" alt="Luxury apartments in Gurgaon" />
      </div>
    </section>
  );
};

export default AboutUs;
