import Navbar from "./components/Navbar";
import Home from "./components/Home";
import Footer from "./components/partials/Footer";
import { BrowserRouter } from "react-router-dom";
import About from "./components/About";
import Products from "./components/Products";
import FloatingForm from "./components/FloatingForm";
import SecondPopupForm from "./components/SecondPopupForm";
import Tile from "./components/Tile";
import { useState, useEffect } from "react";

function App() {
  const [showPopup, setShowPopup] = useState(false); // Control visibility of SecondPopupForm
  const [showFloatingForm, setShowFloatingForm] = useState(false); // Control visibility of FloatingForm

  const handleEnquireClick = () => {
    setShowPopup(true); // Show the enquiry popup
  };

  const handleClosePopup = () => {
    setShowPopup(false); // Close the enquiry popup
  };

  const handleCloseFloatingForm = () => {
    setShowFloatingForm(false); // Close the floating form popup
  };

  useEffect(() => {
    // Automatically show the FloatingForm every 15 seconds
    const interval = setInterval(() => {
      setShowFloatingForm(true);
    }, 15000); // 15 seconds interval

    return () => clearInterval(interval); // Cleanup on unmount
  }, []);

  return (
    <BrowserRouter>
      <>
        <Navbar />
      </>
      <div>
        <Home />
        <Products />
        <About />
        <Footer />
      </div>
      <FloatingForm isVisible={showFloatingForm} closeForm={handleCloseFloatingForm} /> {/* Control floating form */}
      <SecondPopupForm isVisible={showPopup} closeForm={handleClosePopup} /> {/* Control visibility */}
      <Tile handleEnquire={handleEnquireClick} /> {/* Pass the function to Tile */}
    </BrowserRouter>
  );
}

export default App;
