import React, { useState } from 'react';
import '../css/FloatingForm.css'; // Import your custom CSS
import emailjs from '@emailjs/browser';

const FloatingForm = () => {
  const [isMinimized, setIsMinimized] = useState(false);
  const [formData,setFormData]=useState({
    email:"",
    phone
  })

  const minimizeForm = () => {
    setIsMinimized(true);
  };

  const maximizeForm = () => {
    setIsMinimized(false);
  };
  const form =useRe

  // const onSubmit=(e)=>{
    const sendEmail = (e) => {
      e.preventDefault();
  
      emailjs
        .sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', form.current, {
          publicKey: 'YOUR_PUBLIC_KEY',
        })
        .then(
          () => {
            console.log('SUCCESS!');
          },
          (error) => {
            console.log('FAILED...', error.text);
          },
        );
  }

  return (
    <div className="floating-form">
      {!isMinimized && (
        <div className="form-content">
          <button className="close-btn" onClick={minimizeForm}>&#10006;</button>
          <h2>Express Your Interest</h2>
          <form>
            <input type="text" placeholder="Name*" required />
            <input type="email" placeholder="Email*" required />
            <input type="tel" placeholder="Mobile*" required />
            <input type="text" placeholder="Message*"  />
            <button type="submit" onClick={sendEmail}>Submit</button>
          </form>
          {/* <div className="security-badge">
            <img src="/path-to-your-security-logo.png" alt="GoDaddy Secure" />
          </div> */}
        </div>
      )}
      {isMinimized && (
        <div className="minimized-icon" onClick={maximizeForm}>
        <i class="fa-regular fa-envelope icon"></i>
        </div>
      )}
    </div>
  );
};

