import React, { useRef, useState, useEffect } from 'react';
import '../css/SecondPopupForm.css'; // Use a new CSS file for the second popup form
import emailjs from '@emailjs/browser';

const SecondPopupForm = () => {
  const [isVisible, setIsVisible] = useState(false); // Popup visibility state
  const form = useRef();

  useEffect(() => {
    // Show the popup after 10 seconds
    const timer = setTimeout(() => {
      setIsVisible(true);
    }, 10000); // 10 seconds

    return () => clearTimeout(timer); // Cleanup the timer
  }, []);

  const closeForm = () => {
    setIsVisible(false);
  };

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        process.env.REACT_APP_YOUR_SERVICE_ID,
        process.env.REACT_APP_YOUR_TEMPLATE_ID,
        form.current,
        { publicKey: process.env.REACT_APP_YOUR_PUBLIC_KEY }
      )
      .then(
        () => {
          console.log('SUCCESS!');
        },
        (error) => {
          console.log('FAILED...', error.text);
        }
      );
  };

  return (
    <>
      {isVisible && (
        <div className="second-popup-overlay">
          <div className="second-popup-form">
            <button className="close-btn-float" onClick={closeForm}>
              &#10006;
            </button>
            <h2>Sign Up for More Info</h2>
            <form ref={form} onSubmit={sendEmail}>
              <input type="text" placeholder="Name*" name="user_name" required />
              <input type="email" placeholder="Email*" name="user_email" required />
              <input type="tel" placeholder="Mobile*" name="user_mobile" required />
              <textarea type="text" placeholder="Message" name="message" />
              <button type="submit">Submit</button>
            </form>
          </div>
        </div>
      )}
    </>
  );
};

export default SecondPopupForm;
